///////////////////// TAG ROL /////////////////////////////////

client.on("userUpdate", async (oldUser, newUser) => {
  if (oldUser.username !== newUser.username) {
  const tag = ayarlar.tag
  const sunucu = ayarlar.sunucuID
  const kanal = ayarlar.tagRolLogID
  const rol = ayarlar.tagRolID

  try {

  if (newUser.username.includes(tag) && !client.guilds.cache.get(sunucu).members.cache.get(newUser.id).roles.cache.has(rol)) {
  await client.channels.cache.get(kanal).send(new Discord.MessageEmbed().setColor("GREEN").setDescription(`${newUser} ${tag} Tagımızı Aldığı İçin <@&${rol}> Rolünü Verdim`));
  await client.guilds.cache.get(sunucu).members.cache.get(newUser.id).roles.add(rol);
  await client.guilds.cache.get(sunucu).members.cache.get(newUser.id).send(new Discord.MessageEmbed().setColor("GREEN").setDescription(`${newUser.username} │ ${tag} Tagımızı Aldığı İçin ${client.guilds.cache.get(sunucu).roles.cache.get(rol).name} Rolünü Verdim`));
  }
  if (!newUser.username.includes(tag) && client.guilds.cache.get(sunucu).members.cache.get(newUser.id).roles.cache.has(rol)) {
  await client.channels.cache.get(kanal).send(new Discord.MessageEmbed().setColor("RED").setDescription(`${newUser} ${tag} Tagımızı Çıkardığı İçin <@&${rol}> Rolünü Aldım`));
  await client.guilds.cache.get(sunucu).members.cache.get(newUser.id).roles.remove(rol);
  await client.guilds.cache.get(sunucu).members.cache.get(newUser.id).send(new Discord.MessageEmbed().setColor("RED").setDescription(`${newUser.username} │ ${tag} Tagımızı çıkardığın için ${client.guilds.cache.get(sunucu).roles.cache.get(rol).name} Rolünü senden aldım.`));
  }
} catch (e) {
console.log(`Bir hata oluştu! ${e}`)
 }
}
});

